import { useState } from 'react';
import './App.css';
import Choose from './components/Choose/Choose';
import Inform from './components/Inform/Inform';
import Status from './components/Status/Status';
import Table from './components/Table/Table';

function App() {
  const [phase, setPhase] = useState(0);
  //0: betting, 1: opened, 2: result
  const [money, setMoney] = useState(100);
  const [bet, setBet] = useState(0);
  const [dealerDeck, setDealerDeck] = useState([]);
  const [playerDeck, setPlayerDeck] = useState([]);
  return (
    <div className="App">
      <div class="app">
        <Status money={money} setMoney={setMoney} bet={bet} setBet={setBet} phase={phase} setPhase={setPhase}/>
        <Table money={money} setMoney={setMoney} bet={bet} setBet={setBet} phase={phase} setPhase={setPhase}/>
        <Inform money={money} setMoney={setMoney} bet={bet} setBet={setBet} phase={phase} setPhase={setPhase}/>
        <Choose money={money} setMoney={setMoney} bet={bet} setBet={setBet} phase={phase} setPhase={setPhase}/>
      </div>
    </div>
  );
}

export default App;
