import React from "react";
import './Choose.css';

const Choose = ({money, setMoney, bet, setBet, phase, setPhase}) => {
    return(
        <div>
            <div class="chooseBar">
                {phase === 0 ?
                    <div>
                        <div class="button">BET!</div>
                    </div>
                    :
                    <div>
                        <div class="button">HIT</div>
                        <div class="button">STAND</div>
                        <div class="button">SPLIT</div>
                    </div>
                }
            </div>
        </div>
    );
}

export default Choose;
