import { useState } from 'react';
import './Choose.css';

const Choose = ({money, setMoney, bet, setBet, phase, setPhase}) => {
    const [betting, setBetting] = useState(10);
    
    return(
        <div>
            <div class="chooseBar">
                {phase === 0 ?
                    <div class="betPhase">
                        <div class="betAdjust">↑</div>
                        <div class="betAdjust">↓</div>
                        <div class="betting">{betting} G</div>
                        <div class="button">BET!</div>
                    </div>
                    :
                    <div>
                        <div class="button">HIT</div>
                        <div class="button">STAND</div>
                        <div class="button">SPLIT</div>
                    </div>
                }
            </div>
        </div>
    );
}

export default Choose;
