import React from "react";
import './Inform.css';

const Inform = ({money, setMoney, bet, setBet, phase, setPhase}) => {
    return(
        <div>
            <div class="informBar">
                {phase === 0 ? 
                    <div class="content">
                        배팅액을 선택하세요.
                    </div>
                    :
                    <div class="content">
                        액션을 선택하세요.
                    </div>
                }
            </div>
        </div>
    );
}

export default Inform;
